INSERT INTO `VWR INTERNAL Messyvegancook Equipment` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment) VALUES (X'363963DF83AEB947AF603392F5C8F18D', X'56690378FB342841A0B458E18CD0CDD0', X'56690378FB342841A0B458E18CD0CDD0', 0, NULL, NULL, 2, NULL, 0);
INSERT INTO `VWR INTERNAL Messyvegancook howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'EB88439C1EBE6445961CD519352A836D', X'01317A748B813A479C8A7AE15F549F8E', X'01317A748B813A479C8A7AE15F549F8E', 0, NULL, NULL, 2, NULL, 0, 'Add the rice, water, and mushroom seasoning granules/powder to your instant pot bowl. Pressure cook on high for 15 minutes and allow natural pressure release for 10 minutes. Alternatively you can soak the rice overnight, wrap it in muslin, and steam for around 30 minutes, flipping at the 20 minute mark. The timing for the non Instant Pot method may need to be adjusted depending on how long the rice has soaked.');
INSERT INTO `VWR INTERNAL Messyvegancook howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'0FED8FCD85731940A0E7A7B2F7E5348E', X'FC91760FE76B2447B09B11C24BC12C8B', X'FC91760FE76B2447B09B11C24BC12C8B', 0, NULL, NULL, 2, NULL, 0, 'Toss the cubed tofu in the sesame oil and bake at 180 degrees (fan) Celcius for 25 minutes. Remove from the oven, add the dukkah directly to the tray you cooked the tofu in, and shake to coat.');
INSERT INTO `VWR INTERNAL Messyvegancook howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'4F23898CB56FDD419CD206907A739486', X'9F386B4773379F4784BF5FD43B5D6EB1', X'9F386B4773379F4784BF5FD43B5D6EB1', 0, NULL, NULL, 2, NULL, 0, 'Add some oil to a saucepan and fry the garlic and onion until the onion is translucent. Add the tinned tomato. Cook for 15-20 minutes on medium-low heat until the sauce thickens.');
INSERT INTO `VWR INTERNAL Messyvegancook howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'5B3DA53410BF034D95779E875B5C96F5', X'9F386B4773379F4784BF5FD43B5D6EB1', X'9F386B4773379F4784BF5FD43B5D6EB1', 1, NULL, NULL, 2, NULL, 0, 'While you are cooking the tomato sauce, tip the dessicated coconut into a small frying pan. Toast over medium-low heat until evenly browned.');
INSERT INTO `VWR INTERNAL Messyvegancook howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'54911081BDEA084F828DB6217A1CEFE9', X'9F386B4773379F4784BF5FD43B5D6EB1', X'9F386B4773379F4784BF5FD43B5D6EB1', 2, NULL, NULL, 2, NULL, 0, 'When the tomato sauce is cooked, tip it into a blender, along with the toasted coconut and rose water. Purée to a smooth consistency. Tip back into the saucepan (you can rise the blender jug out with a wee bit of water to add back to the sauce too). Add salt to taste and keep on a low heat until you''re ready to serve.');
INSERT INTO `VWR INTERNAL Messyvegancook howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'0F9497D256CDCD4EB40917135DE10EFA', X'8F5A580108FAD54BB41587D5822F67FB', X'8F5A580108FAD54BB41587D5822F67FB', 0, NULL, NULL, 2, NULL, 0, 'Divide the rice into two portions and place in the centre of a plate or large, wide bowl like a soup plate. You can shape it with a cup if you''d like. Pour the tomato mash around the rice (it''s quite thick so you can use a spoon or fork to flatten/even it out). Drizzle the rice and tomato with a little bit of pomegranate molasses.');
INSERT INTO `VWR INTERNAL Messyvegancook howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'EF790CD780341142AED24B66BCA14BE4', X'8F5A580108FAD54BB41587D5822F67FB', X'8F5A580108FAD54BB41587D5822F67FB', 1, NULL, NULL, 2, NULL, 0, 'Carefully scoop half the tofu with a wedge of the cheese onto each mound of rice and top with herbs. You can also drizzle some extra virgin olive oil over the lot.');
INSERT INTO `VWR INTERNAL Messyvegancook howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'FF8DEEF8A8015744A797594BBF2CFFAC', X'507EBD562142554DAF1568652975ADAE', X'507EBD562142554DAF1568652975ADAE', 0, NULL, NULL, 2, NULL, 0, 'Toast the rice and cinnamon stick together in a dry pan until the rice is golden and emits a nutty aroma1. Tip into a large bowl, along with the almonds and water. Cover with a plate or towel and leave to soak overnight.');
INSERT INTO `VWR INTERNAL Messyvegancook howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'CD761C827C90A54EBF4D2DBC2A05D27F', X'507EBD562142554DAF1568652975ADAE', X'507EBD562142554DAF1568652975ADAE', 1, NULL, NULL, 2, NULL, 0, 'The next morning, tip all of the contents of the bowl into a blender jug. Unless you have a powerful vortex blender such as a Vitamix, remove the cinnamon first. Add the sugar, vanilla extract, optional almond extract, and salt. Blend on high speed until the rice and almonds have broken down as finely as possible and the sugar has dissolved. Taste for sweetness and add more sugar if desired.');
INSERT INTO `VWR INTERNAL Messyvegancook howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D0F5B140AD99F847BFD988A5B2A14B40', X'507EBD562142554DAF1568652975ADAE', X'507EBD562142554DAF1568652975ADAE', 2, NULL, NULL, 2, NULL, 0, 'Strain using either a very fine mesh or a muslin cloth. To avoid wasting the pulp, see my notes for how you can use it2.');
INSERT INTO `VWR INTERNAL Messyvegancook howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'1B2F928009607348932110B3E20B4FD4', X'507EBD562142554DAF1568652975ADAE', X'507EBD562142554DAF1568652975ADAE', 3, NULL, NULL, 2, NULL, 0, 'Store in the fridge. Horchata is best served ice cold, so to serve just fill a glass with ice and then pour horchata to the brim.');
INSERT INTO `VWR INTERNAL Messyvegancook howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'A81BE4F75EC5554B98647967F65E7EA4', X'24C9234D5823BC4C96642DE0B7E08D77', X'24C9234D5823BC4C96642DE0B7E08D77', 0, NULL, NULL, 2, NULL, 0, 'Make the recipe as above, but remove the cinnamon before blending. You can also omit the vanilla and almond extract if you’d like (it’s good either way).');
INSERT INTO `VWR INTERNAL Messyvegancook howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'3B61D90B0087A646A71341D4D8242319', X'24C9234D5823BC4C96642DE0B7E08D77', X'24C9234D5823BC4C96642DE0B7E08D77', 1, NULL, NULL, 2, NULL, 0, 'Once the horchata is strained, add around 250 millilitres (1 cup) of the liquid back to the blender, along with 4-6 pandan leaves that have been cut into 1 ½ centimetre (½ inch) pieces. Blend on high speed until the leaves break up as finely as possible and the horchata turns green.');
INSERT INTO `VWR INTERNAL Messyvegancook howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'69B7441E17923245807EE8956BD21A92', X'24C9234D5823BC4C96642DE0B7E08D77', X'24C9234D5823BC4C96642DE0B7E08D77', 2, NULL, NULL, 2, NULL, 0, 'Use muslin to strain this liquid back into the rest of the horchata. Discard the pandan leaf pulp.');
INSERT INTO `VWR INTERNAL Messyvegancook howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B580356E92DA7448B5DC2BDBBDAFE510', X'FFD64BF332B51E4AB14024DAD8DE75C7', X'FFD64BF332B51E4AB14024DAD8DE75C7', 0, NULL, NULL, 2, NULL, 0, 'Add 2-4 tablespoons khao mak in the bottom of your glass before topping up with ice and horchata. It’s fine enough to drink with a normal straw or you can eat it with a spoon.');
INSERT INTO `VWR INTERNAL Messyvegancook howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'77F443998FC62642BBF190712112652D', X'D1EA5422B3D9F8489C9487CDC47D3396', X'D1EA5422B3D9F8489C9487CDC47D3396', 0, NULL, NULL, 2, NULL, 0, 'Toast the coriander seeds and cumin seeds over medium heat by shaking them around in a pan for until fragrant (30-60 seconds). Grind to a powder, along with the white peppercorns, using a pestle and mortar. Remove the powder to a smaller bowl.');
INSERT INTO `VWR INTERNAL Messyvegancook howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'64A7876276E3574FACE7823B9CC0FF2A', X'D1EA5422B3D9F8489C9487CDC47D3396', X'D1EA5422B3D9F8489C9487CDC47D3396', 1, NULL, NULL, 2, NULL, 0, 'Pound the salt and chillies to a paste in the mortar, followed by the lemongrass. Continue in the order of ingredients listed, ensuring each is pounded into a smooth paste before adding the next. This may take 20-30 minutes.');
INSERT INTO `VWR INTERNAL Messyvegancook howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'687539D55A28A940A1A91900B0132FFD', X'D1EA5422B3D9F8489C9487CDC47D3396', X'D1EA5422B3D9F8489C9487CDC47D3396', 2, NULL, NULL, 2, NULL, 0, 'When the paste is smooth, add the toasted ground spices and pound together.');
INSERT INTO `VWR INTERNAL Messyvegancook howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'9222328AF8509F4CBA98AC3122124157', X'FE58FC50E23ECF4AAD8D5E8E42EA43F4', X'FE58FC50E23ECF4AAD8D5E8E42EA43F4', 0, NULL, NULL, 2, NULL, 0, 'Slice the aubergines in halves or quarters, depending on size, and place in a bowl with a teaspoon of lemon/lime juice or citric acid. This step is optional but will help keep the eggplant from discolouring.');
INSERT INTO `VWR INTERNAL Messyvegancook howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C0FC5A0F2E8A5A48B2211553B4FE4E5B', X'FE58FC50E23ECF4AAD8D5E8E42EA43F4', X'FE58FC50E23ECF4AAD8D5E8E42EA43F4', 1, NULL, NULL, 2, NULL, 0, 'Heat the thick coconut milk/cream and oil in a saucepan on medium high heat, stirring frequently, until the water begins to evaporate off and the liquid begins to separate into oily pools (the surface will appear initially as a thicker white solids with little holes). If this doesn’t happen after 8-10 minutes, just carry on with the recipe anyway.');
INSERT INTO `VWR INTERNAL Messyvegancook howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B4DE047A3FBA4C4CAC85C00114122A53', X'FE58FC50E23ECF4AAD8D5E8E42EA43F4', X'FE58FC50E23ECF4AAD8D5E8E42EA43F4', 2, NULL, NULL, 2, NULL, 0, 'Knock the heat down to medium-low and add the curry paste, stirring to coat in the oil. Continue to cook for about 5 minutes, stirring constantly, until the smell of raw garlic and shallot has cooked off.');
INSERT INTO `VWR INTERNAL Messyvegancook howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F79CFC3B06E2AC40A8625EE36AAE1651', X'FE58FC50E23ECF4AAD8D5E8E42EA43F4', X'FE58FC50E23ECF4AAD8D5E8E42EA43F4', 3, NULL, NULL, 2, NULL, 0, 'Add the thinner coconut milk, lime leaves, palm sugar, soy sauce, and rehydrated TVP. Bring the curry to a boil and then turn the heat down to maintain a simmer (small, occasional bubbles).');
INSERT INTO `VWR INTERNAL Messyvegancook howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'8024E292005E324EA58C08360C95E662', X'FE58FC50E23ECF4AAD8D5E8E42EA43F4', X'FE58FC50E23ECF4AAD8D5E8E42EA43F4', 4, NULL, NULL, 2, NULL, 0, 'Drain away the acidulated water from the aubergines and add them to the curry. Cook for 10-15 minutes, until the eggplants are tender but not mushy. They should still have a little bit of crunch to them.');
INSERT INTO `VWR INTERNAL Messyvegancook howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'D598B48FD85F9A44AE3CBAAF7E6BC85C', X'FE58FC50E23ECF4AAD8D5E8E42EA43F4', X'FE58FC50E23ECF4AAD8D5E8E42EA43F4', 5, NULL, NULL, 2, NULL, 0, 'The flavour profile is meant to be salty and spicy, with a hint of sweetness. Taste for salt and season with additional soy sauce to taste.');
INSERT INTO `VWR INTERNAL Messyvegancook howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'5C26D9C4F2898B4599952465F84BD5E6', X'FE58FC50E23ECF4AAD8D5E8E42EA43F4', X'FE58FC50E23ECF4AAD8D5E8E42EA43F4', 6, NULL, NULL, 2, NULL, 0, 'Add sliced chillies and basil leaves. Serve with jasmine rice.');
INSERT INTO `VWR INTERNAL Messyvegancook howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'02463660C2A49E4F85DCE01285B88517', X'197ED9D6DF36154EB873B5AA8F5FDF2C', X'197ED9D6DF36154EB873B5AA8F5FDF2C', 0, NULL, NULL, 2, NULL, 0, 'Steam the peppers, garlic, and coriander root for 15 minutes over medium-high heat.');
INSERT INTO `VWR INTERNAL Messyvegancook howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'7F4539C2C000EA4E9ED14DAEBA721DA7', X'197ED9D6DF36154EB873B5AA8F5FDF2C', X'197ED9D6DF36154EB873B5AA8F5FDF2C', 1, NULL, NULL, 2, NULL, 0, 'Blend with remaining ingredients in a high powered blender until smooth.');
INSERT INTO `VWR INTERNAL Messyvegancook howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'7B5CFAE4474CB84B8D58004B5E41277B', X'197ED9D6DF36154EB873B5AA8F5FDF2C', X'197ED9D6DF36154EB873B5AA8F5FDF2C', 2, NULL, NULL, 2, NULL, 0, 'Pour into a small saucepan and simmer over low heat, stirring from time to time, for 15 minutes. Allow to cool and then jar and refrigerate.');
INSERT INTO `VWR INTERNAL Messyvegancook howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'36D767ACDE4E874693B39B87B3720D8D', X'AC39E6E3ECE3DE44B8A6C5E2D85063D0', X'AC39E6E3ECE3DE44B8A6C5E2D85063D0', 0, NULL, NULL, 2, NULL, 0, 'Add the fermented tofu, brine, garlic, vinegar, sugar, ketchup, and salt to a small saucepan. Cook on medium low for about 10 minutes, stirring often. When cooled slightly, blend until smooth. You can now add beetroot powder (or pink dye) if you wish for a brighter pink colour. Decant into a jar. This can be kept, refrigerated, for at least a week (realistically a few).');
INSERT INTO `VWR INTERNAL Messyvegancook howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C6F396FEB551C647B991733DA2903BE8', X'EAFAFA9A6433A04DA0F60CFC8F79946F', X'EAFAFA9A6433A04DA0F60CFC8F79946F', 0, NULL, NULL, 2, NULL, 0, 'Add the yentafo sauce, soy sauce, fried garlic in garlic oil, and salt to a serving bowl.');
INSERT INTO `VWR INTERNAL Messyvegancook howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'77CA8DF5FB35CD439C403487B1187372', X'EAFAFA9A6433A04DA0F60CFC8F79946F', X'EAFAFA9A6433A04DA0F60CFC8F79946F', 1, NULL, NULL, 2, NULL, 0, 'Fill a saucepan deep enough to submerge the contents of a noodle basket with water and bring to a rapid boil.');
INSERT INTO `VWR INTERNAL Messyvegancook howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B040E40A77CB0A41BE10912CF49FD582', X'EAFAFA9A6433A04DA0F60CFC8F79946F', X'EAFAFA9A6433A04DA0F60CFC8F79946F', 2, NULL, NULL, 2, NULL, 0, 'Add the tofu puffs, fish balls, mock squid, and white fungus to a saucepan with your stock (you can use the pan you used to make yentafo sauce – no need to rinse out). Bring to a boil and knock the heat down to medium low. If the fish balls and tofu puffs are frozen, they’ll need to cook for 5-10 minutes, until the fish balls float. Otherwise they’ll take a minute or so.');
INSERT INTO `VWR INTERNAL Messyvegancook howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'2CFE15E75183CA41B08A4B8D57CC4171', X'EAFAFA9A6433A04DA0F60CFC8F79946F', X'EAFAFA9A6433A04DA0F60CFC8F79946F', 3, NULL, NULL, 2, NULL, 0, 'Place noodles, water spinach, and bean sprouts into a noodle basket. Plunge the basket into the boiling water and cook, agitating with chopsticks to ensure even cooking, for between 30-60 seconds. Pull the basket out of the water and shake the basket to offload excess water (if you’re not comfortable doing this without making a mess you can move the basket over a large bowl and shake within the confines of this splashproof space).');
INSERT INTO `VWR INTERNAL Messyvegancook howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'B0A55D4DF3A77C4193150E642FB4A760', X'EAFAFA9A6433A04DA0F60CFC8F79946F', X'EAFAFA9A6433A04DA0F60CFC8F79946F', 4, NULL, NULL, 2, NULL, 0, 'Tip the contents of the noodle baskets into the prepped serving bowl. Pour the contents of the stock pan over the noodles. Garnish with the fresh chopped herbs and a fried wonton or seven. Serve with chilli vinegar, soy sauce or soy sauce soaked chillies, sugar, and toasted chilli powder as condiments.');
INSERT INTO `VWR INTERNAL Messyvegancook howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'F689B1522B1F064CA28D37607D42DDE7', X'4CE0D18BEA4B7945BF59DD7690DD773C', X'4CE0D18BEA4B7945BF59DD7690DD773C', 0, NULL, NULL, 2, NULL, 0, 'Soak the rice noodles in warm water for one hour. Boiling them to cook aids in their demise, so don’t be tempted unless you’re okay with soggy noodles. If you are, no judgement because sometimes I like them too.');
INSERT INTO `VWR INTERNAL Messyvegancook howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'20FE940069B7204E8366CC07A3F69FC5', X'4CE0D18BEA4B7945BF59DD7690DD773C', X'4CE0D18BEA4B7945BF59DD7690DD773C', 1, NULL, NULL, 2, NULL, 0, 'Make tamarind water by mashing the tamarind pulp and 45 millilitres of hot water in a small bowl. Fork and/or hands will suffice. Use the back of a wooden spoon or silicone spatula to press the pulp against a mesh strainer, sieving the thick-ish liquid into another small bowl. Be sure to scrape the thick goop from the bottom of the strainer. Discard the solids left in the strainer. Mix the palm sugar, soy sauce, and salt into the tamarind water.');
INSERT INTO `VWR INTERNAL Messyvegancook howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'31EEF77164CA5044B89B1C0760775973', X'4CE0D18BEA4B7945BF59DD7690DD773C', X'4CE0D18BEA4B7945BF59DD7690DD773C', 2, NULL, NULL, 2, NULL, 0, 'Once the noodles are soaked (the consistency will be bouncy with a little firmness, and they won’t taste like they’re ready to eat yet), heat a wok to medium high heat. Once hot, add the oil.');
INSERT INTO `VWR INTERNAL Messyvegancook howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'260830A6CD663B48A7B1DB379C020F76', X'4CE0D18BEA4B7945BF59DD7690DD773C', X'4CE0D18BEA4B7945BF59DD7690DD773C', 3, NULL, NULL, 2, NULL, 0, 'Stir fry the tofu, shallot, and preserved radish for a few minutes, just until the tofu begins to colour. Add the noodles and the tamarind sauce, continuing to stir fry for about a minute. If the noodles don’t soften a little under the heat, toss a tablespoon of water in to aid in softening.');
INSERT INTO `VWR INTERNAL Messyvegancook howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'C4D7467FBE86C548BC057200BBC2284E', X'4CE0D18BEA4B7945BF59DD7690DD773C', X'4CE0D18BEA4B7945BF59DD7690DD773C', 4, NULL, NULL, 2, NULL, 0, 'Add most of the bean sprouts and Chinese chives, stir-frying just until wilted.');
INSERT INTO `VWR INTERNAL Messyvegancook howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeInstruction) VALUES (X'6C7BDB2560E68544A8D5A6EF7966619F', X'4CE0D18BEA4B7945BF59DD7690DD773C', X'4CE0D18BEA4B7945BF59DD7690DD773C', 5, NULL, NULL, 2, NULL, 0, 'Dish up, garnishing the noodles with the remaining sprouts and chives, plus a sprinkling of peanuts and toasted chilli powder. Serve with a slice of lime.');
