INSERT INTO `VWR INTERNAL Messyvegancook ingredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngedient) VALUES (X'D420F5817B49654991FE2D5972ADF71B', X'8E7CECDF0156924F933B2CB6835194C0', X'8E7CECDF0156924F933B2CB6835194C0', 11, NULL, NULL, 2, NULL, 0, 'Small broken off piece dried white fungus');
INSERT INTO `VWR INTERNAL Messyvegancook ingredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngedient) VALUES (X'73C4C6C8C81A564788AFDE5582B661D8', X'8E7CECDF0156924F933B2CB6835194C0', X'8E7CECDF0156924F933B2CB6835194C0', 12, NULL, NULL, 2, NULL, 0, '1 tablespoon chopped mix of one or more of spring onion, coriander, Chinese celery');
INSERT INTO `VWR INTERNAL Messyvegancook ingredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngedient) VALUES (X'CB96DCADEFC96440977CEE3AFCC72C90', X'8E7CECDF0156924F933B2CB6835194C0', X'8E7CECDF0156924F933B2CB6835194C0', 13, NULL, NULL, 2, NULL, 0, 'Fried wonton skins see notes5');
INSERT INTO `VWR INTERNAL Messyvegancook ingredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngedient) VALUES (X'5F64AC0BAA45AE49962C85453456BB8E', X'8E7CECDF0156924F933B2CB6835194C0', X'8E7CECDF0156924F933B2CB6835194C0', 14, NULL, NULL, 2, NULL, 0, 'kruang prung of chilli vinegar, soy sauce or chillies soaked in soy sauce, sugar, prik bon see notes6');
INSERT INTO `VWR INTERNAL Messyvegancook ingredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngedient) VALUES (X'E63C895B2DE45A4880C391A5ABA7BE0A', X'CA7D3C5B596C794C9D818401E08CC698', X'CA7D3C5B596C794C9D818401E08CC698', 0, NULL, NULL, 2, NULL, 0, 'To make the tomato water, blend the tomatoes and water to a thin consistency. Pour into a small saucepan, add salt, and bring to the boil. Reduce the heat to medium low and cook for 15 minutes.');
INSERT INTO `VWR INTERNAL Messyvegancook ingredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngedient) VALUES (X'328066F4A818C24ABC9CDA984004D1BB', X'CA7D3C5B596C794C9D818401E08CC698', X'CA7D3C5B596C794C9D818401E08CC698', 1, NULL, NULL, 2, NULL, 0, 'To make the ginger water, pound the ginger with a pestle and mortar until broken down into a paste. Add the water and muddle around until combined.');
INSERT INTO `VWR INTERNAL Messyvegancook ingredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngedient) VALUES (X'3EF55921362BB544B3576D35D7B5BA40', X'CA7D3C5B596C794C9D818401E08CC698', X'CA7D3C5B596C794C9D818401E08CC698', 2, NULL, NULL, 2, NULL, 0, 'For the sour water, add the water, vinegar, and jaggery to small saucepan and heat until the sugar is dissolved.');
INSERT INTO `VWR INTERNAL Messyvegancook ingredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngedient) VALUES (X'EBC1174DEE93DE47AA3D329C8DCFAA68', X'CA7D3C5B596C794C9D818401E08CC698', X'CA7D3C5B596C794C9D818401E08CC698', 3, NULL, NULL, 2, NULL, 0, 'If your noodles are not completely fresh (i.e. made that day and never refrigerated), you will need to cook them briefly. To do this, bring a pot of water to a rapid boil. Use a noodle basket or a mesh strainer to dunk the noodles in the boiling pot before tipping into the serving bowls. Do this one portion at a time. If you haven’t already blanched the bean sprouts, you can do so using this same pot of boiling water.');
INSERT INTO `VWR INTERNAL Messyvegancook ingredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngedient) VALUES (X'381E25DBAE593A4586E6307372468C6C', X'CA7D3C5B596C794C9D818401E08CC698', X'CA7D3C5B596C794C9D818401E08CC698', 4, NULL, NULL, 2, NULL, 0, 'Distribute the tomato water, sour water, and ginger water over the noodles. Add ground sesame seeds and peanuts, along with the prik jeow, fried garlic, and a slight drizzle of the black soy sauce. Top with the cabbage, blanched sprouts, and herbs.');
INSERT INTO `VWR INTERNAL Messyvegancook ingredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngedient) VALUES (X'2DAA1C1B43AB214BA251F63EEF05772E', X'CA7D3C5B596C794C9D818401E08CC698', X'CA7D3C5B596C794C9D818401E08CC698', 5, NULL, NULL, 2, NULL, 0, 'Serve with additional salt and some MSG.');
INSERT INTO `VWR INTERNAL Messyvegancook ingredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngedient) VALUES (X'717518CCDC410143B0D1813512BABDDD', X'78D1EA321EDBA540B4D806824601EB2F', X'78D1EA321EDBA540B4D806824601EB2F', 0, NULL, NULL, 2, NULL, 0, 'Whisk dry ingredients together in a medium bowl.');
INSERT INTO `VWR INTERNAL Messyvegancook ingredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngedient) VALUES (X'730ECC4DACDD7342B2C814192C6B6AA3', X'78D1EA321EDBA540B4D806824601EB2F', X'78D1EA321EDBA540B4D806824601EB2F', 1, NULL, NULL, 2, NULL, 0, 'Combine remaining ingredients in a small bowl. Pour into flour and stir with your fingers until it comes together, just beyond shaggy. Leave to rest for 10 minutes.');
INSERT INTO `VWR INTERNAL Messyvegancook ingredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngedient) VALUES (X'ECC46F9F40D60D4BA77D79FDDADD222F', X'78D1EA321EDBA540B4D806824601EB2F', X'78D1EA321EDBA540B4D806824601EB2F', 2, NULL, NULL, 2, NULL, 0, 'Heat a small pot of water on low, not boiling. When hot, tear the seitan pieces into the size you’d like and add them to the water. Cook for 10 minutes and drain.');
INSERT INTO `VWR INTERNAL Messyvegancook ingredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngedient) VALUES (X'28D7F11BA04B934CBBAD62E7255FC408', X'8ECBD1CE4E3DDB459E19CEE18B54E594', X'8ECBD1CE4E3DDB459E19CEE18B54E594', 0, NULL, NULL, 2, NULL, 0, 'Cut the tofu into approximate 2-3 centimetre pieces. Don’t worry too much about getting this perfect. Dump the cubes into a heat proof bowl or saucepan. Pour the just boiled water over to cover. Cover and leave for 15 minutes. Strain and lay the tofu on a tea towel to dry, patting the edges to soak up water.');
INSERT INTO `VWR INTERNAL Messyvegancook ingredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngedient) VALUES (X'43942A6CD79C58488A690F2156544BF6', X'8ECBD1CE4E3DDB459E19CEE18B54E594', X'8ECBD1CE4E3DDB459E19CEE18B54E594', 1, NULL, NULL, 2, NULL, 0, 'You can either shallow or deep fry the tofu. I prefer the latter. Add enough oil to cover the tofu and then some into a wok. Knock the heat up to medium high. Add the tofu and fry, turning the pieces from time to time with chopsticks or a holey-spoon (technical term for spider strainer), until puffy and golden. How long you fry is kind of down to personal preference. It’s going to be good no matter what. Remove with holey-spoon and leave to drain on kitchen roll or a clean tea towel you’ll immediately put in the wash after this.');
INSERT INTO `VWR INTERNAL Messyvegancook ingredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngedient) VALUES (X'8B55D362839BE146B2AB0014A83F7E1F', X'8ECBD1CE4E3DDB459E19CEE18B54E594', X'8ECBD1CE4E3DDB459E19CEE18B54E594', 2, NULL, NULL, 2, NULL, 0, 'If you prefer to shallow fry then add a bit of oil to a nonstick pan and fry the tofu on all sides until crispy and browned. Drain on kitchen roll or tea towel as above.');
INSERT INTO `VWR INTERNAL Messyvegancook ingredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngedient) VALUES (X'B9228D1B9169204C9323F288CE36CBD2', X'3E31AE64E8D3DA47AC7338D83F7823E5', X'3E31AE64E8D3DA47AC7338D83F7823E5', 0, NULL, NULL, 2, NULL, 0, 'Soak the rice noodles in lukewarm water for 30 minutes.');
INSERT INTO `VWR INTERNAL Messyvegancook ingredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngedient) VALUES (X'D260CA823C1CB442930D45D29B719E74', X'3E31AE64E8D3DA47AC7338D83F7823E5', X'3E31AE64E8D3DA47AC7338D83F7823E5', 1, NULL, NULL, 2, NULL, 0, 'Find a pot that’s deep enough for at least most of your noodle basket to be submerged into. Fill the pot with water and bring to a rolling boil.');
INSERT INTO `VWR INTERNAL Messyvegancook ingredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngedient) VALUES (X'846BC67574A7024A8AA178EB0FA91B06', X'3E31AE64E8D3DA47AC7338D83F7823E5', X'3E31AE64E8D3DA47AC7338D83F7823E5', 2, NULL, NULL, 2, NULL, 0, 'Meanwhile heat the stock in another pan and keep it hot. If you are using meatballs you can cook them in this stock while getting everything else ready.');
INSERT INTO `VWR INTERNAL Messyvegancook ingredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngedient) VALUES (X'C7DA84408A097E42AECAF5B0A2331960', X'3E31AE64E8D3DA47AC7338D83F7823E5', X'3E31AE64E8D3DA47AC7338D83F7823E5', 3, NULL, NULL, 2, NULL, 0, 'Ready your serving bowl(s). You can pre-season with a splash of of soy sauce if you’d like. Or not.');
INSERT INTO `VWR INTERNAL Messyvegancook ingredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngedient) VALUES (X'77CD0457E41DC947A868E684D4FB0EF1', X'3E31AE64E8D3DA47AC7338D83F7823E5', X'3E31AE64E8D3DA47AC7338D83F7823E5', 4, NULL, NULL, 2, NULL, 0, 'Grab a handful of bean sprouts and the morning glory stems and place them in the basket, followed by the noodles. If you have soaked enough noodles for multiple bowls, you can guestimate by grabbing a small handful. Guess how much you think you should add and then add a little less than that.');
INSERT INTO `VWR INTERNAL Messyvegancook ingredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngedient) VALUES (X'748217678FDA664885FE4925419026F4', X'3E31AE64E8D3DA47AC7338D83F7823E5', X'3E31AE64E8D3DA47AC7338D83F7823E5', 5, NULL, NULL, 2, NULL, 0, 'Dunk the basket into the boiling water. Don’t worry if the basket doesn’t submerge all the way. You can turn the noodles with chopsticks in your dominant hand while holding the basket with the other, to ensure everything is cooked. The cooking time is fast, only a moment or two. The vegetables only need to be blanched and the noodles take only seconds to cook.');
INSERT INTO `VWR INTERNAL Messyvegancook ingredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngedient) VALUES (X'E378653A0D6D484EAA4CE8A29204634F', X'3E31AE64E8D3DA47AC7338D83F7823E5', X'3E31AE64E8D3DA47AC7338D83F7823E5', 6, NULL, NULL, 2, NULL, 0, 'Remove the basket and shake it at a slight angle to remove excess water. Tip the noodles/veggies into a bowl. Add 2-3 pieces tofu, 2-3 tablespoons mock pork, and the vegan meatballs, if using. Pour the stock over the noodles. Spoon a teaspoon of garlic oil (don’t forget to include bits of garlic too) on top, followed by the herbs.');
INSERT INTO `VWR INTERNAL Messyvegancook ingredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngedient) VALUES (X'D88A07C17FA271458CE40D38C6926314', X'3E31AE64E8D3DA47AC7338D83F7823E5', X'3E31AE64E8D3DA47AC7338D83F7823E5', 7, NULL, NULL, 2, NULL, 0, 'Serve with suggested condiments. You will definitely want to add some of the soy sauce. My personal preference is 4-6 teaspoons of soy sauce, with just a few of the chillies; 2-3 teaspoons chilli vinegar; about ¼ teaspoon sugar; and up to a teaspoon of toasted chilli powder. Experiment until you''re happy with the flavour.');
INSERT INTO `VWR INTERNAL Messyvegancook ingredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngedient) VALUES (X'0E2D842610128445B80DF4D2FDE6C3C7', X'2199D50DE8F99548BD6A583AA219D295', X'2199D50DE8F99548BD6A583AA219D295', 0, NULL, NULL, 2, NULL, 0, 'Overview of Thai soy sauces and seasoning sauces');
INSERT INTO `VWR INTERNAL Messyvegancook ingredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngedient) VALUES (X'98464758A52C5F498801AF08EB0B53B8', X'2199D50DE8F99548BD6A583AA219D295', X'2199D50DE8F99548BD6A583AA219D295', 1, NULL, NULL, 2, NULL, 0, 'Vegan alternatives to fish sauce, shrimp paste, and oyster sauce in Thai cuisine');
INSERT INTO `VWR INTERNAL Messyvegancook ingredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngedient) VALUES (X'848DE8DCCA5A114893047A90FC202F61', X'C11157DAC4C2F042AF34E6DBD92937DF', X'C11157DAC4C2F042AF34E6DBD92937DF', 0, NULL, NULL, 2, NULL, 0, 'Add everything to a large stock pot and bring to the boil. You can make a sort of bouquet garni for the coriander, garlic, and peppercorns with a piece of muslin, but this is optional (just tidier on straining). Turn the heat to low and simmer for 45-60 minutes with the lid on. I use the lowest setting on my burner.');
INSERT INTO `VWR INTERNAL Messyvegancook ingredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngedient) VALUES (X'9BE3B5338F80634AB16F344329873554', X'C11157DAC4C2F042AF34E6DBD92937DF', X'C11157DAC4C2F042AF34E6DBD92937DF', 1, NULL, NULL, 2, NULL, 0, 'Turn the heat off and leave to cool. Strain and refrigerate. Squeeze the excess stock from the mushrooms and muslin. Don''t bin the mushrooms (see notes below). You can also freeze this stock.');
INSERT INTO `VWR INTERNAL Messyvegancook ingredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngedient) VALUES (X'D0A61CED67D1674A8959A9461B725ADF', X'7001AAFACEAB7B4CACE1170C2B791394', X'7001AAFACEAB7B4CACE1170C2B791394', 0, NULL, NULL, 2, NULL, 0, 'For each sandwich you will need two pieces of bread. Place a scoop of ice cream on one slice of bread. The quantity depends on how large the slice is, but when you fold it upwards the tallest corners should come above the halfway height of the scoop. <img width=`500` height=`333` src=`https://www.messyvegancook.com/wp-content/uploads/2020/06/IMG_1774-500x333.jpg` class=`attachment-medium size-medium` alt=`Ice cream on bread` srcset=`https://www.messyvegancook.com/wp-content/uploads/2020/06/IMG_1774-500x333.jpg 500w, https://www.messyvegancook.com/wp-content/uploads/2020/06/IMG_1774-1024x683.jpg 1024w, https://www.messyvegancook.com/wp-content/uploads/2020/06/IMG_1774-768x512.jpg 768w, https://www.messyvegancook.com/wp-content/uploads/2020/06/IMG_1774-1536x1024.jpg 1536w, https://www.messyvegancook.com/wp-content/uploads/2020/06/IMG_1774-600x400.jpg 600w, https://www.messyvegancook.com/wp-content/uploads/2020/06/IMG_1774-720x480.jpg 720w, https://www.messyvegancook.com/wp-content/uploads/2020/06/IMG_1774-360x240.jpg 360w, https://www.messyvegancook.com/wp-content/uploads/2020/06/IMG_1774-180x120.jpg 180w, https://www.messyvegancook.com/wp-content/uploads/2020/06/IMG_1774.jpg 1800w` sizes=`(max-width: 500px) 100vw, 500px` />');
INSERT INTO `VWR INTERNAL Messyvegancook ingredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngedient) VALUES (X'6079932354B8024A886D6AD00591A757', X'7001AAFACEAB7B4CACE1170C2B791394', X'7001AAFACEAB7B4CACE1170C2B791394', 1, NULL, NULL, 2, NULL, 0, 'Use a piece of foil to help press the bread around the ice cream. <img width=`500` height=`333` src=`https://www.messyvegancook.com/wp-content/uploads/2020/06/IMG_1779-500x333.jpg` class=`attachment-medium size-medium` alt=`ice cream being wrapped in bread` srcset=`https://www.messyvegancook.com/wp-content/uploads/2020/06/IMG_1779-500x333.jpg 500w, https://www.messyvegancook.com/wp-content/uploads/2020/06/IMG_1779-1024x683.jpg 1024w, https://www.messyvegancook.com/wp-content/uploads/2020/06/IMG_1779-768x512.jpg 768w, https://www.messyvegancook.com/wp-content/uploads/2020/06/IMG_1779-1536x1024.jpg 1536w, https://www.messyvegancook.com/wp-content/uploads/2020/06/IMG_1779-600x400.jpg 600w, https://www.messyvegancook.com/wp-content/uploads/2020/06/IMG_1779-720x480.jpg 720w, https://www.messyvegancook.com/wp-content/uploads/2020/06/IMG_1779-360x240.jpg 360w, https://www.messyvegancook.com/wp-content/uploads/2020/06/IMG_1779-180x120.jpg 180w, https://www.messyvegancook.com/wp-content/uploads/2020/06/IMG_1779.jpg 1800w` sizes=`(max-width: 500px) 100vw, 500px` />');
INSERT INTO `VWR INTERNAL Messyvegancook ingredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngedient) VALUES (X'036ECCAA0336BF4A93D5722C334C8440', X'7001AAFACEAB7B4CACE1170C2B791394', X'7001AAFACEAB7B4CACE1170C2B791394', 2, NULL, NULL, 2, NULL, 0, 'Cover with another piece of bread, ensuring all of the ice cream is encapsulated in the slices. Some areas will overlap more than others. Carefully squeeze the foil around the icecream, pressing lightly to seal the overlaps. If there is any exposed ice cream, cover it with a little tear of additional bread. The bread doesn''t need to be mashed flat. So long as the ice cream is unexposed the freezing process will hold the bread in place so no ice cream leaches out when it is fried. <img width=`500` height=`333` src=`https://www.messyvegancook.com/wp-content/uploads/2020/06/IMG_1780-500x333.jpg` class=`attachment-medium size-medium` alt=`` srcset=`https://www.messyvegancook.com/wp-content/uploads/2020/06/IMG_1780-500x333.jpg 500w, https://www.messyvegancook.com/wp-content/uploads/2020/06/IMG_1780-1024x683.jpg 1024w, https://www.messyvegancook.com/wp-content/uploads/2020/06/IMG_1780-768x512.jpg 768w, https://www.messyvegancook.com/wp-content/uploads/2020/06/IMG_1780-1536x1024.jpg 1536w, https://www.messyvegancook.com/wp-content/uploads/2020/06/IMG_1780-600x400.jpg 600w, https://www.messyvegancook.com/wp-content/uploads/2020/06/IMG_1780-720x480.jpg 720w, https://www.messyvegancook.com/wp-content/uploads/2020/06/IMG_1780-360x240.jpg 360w, https://www.messyvegancook.com/wp-content/uploads/2020/06/IMG_1780-180x120.jpg 180w, https://www.messyvegancook.com/wp-content/uploads/2020/06/IMG_1780.jpg 1800w` sizes=`(max-width: 500px) 100vw, 500px` />');
INSERT INTO `VWR INTERNAL Messyvegancook ingredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngedient) VALUES (X'8A6470A20AF5954D850FD6D8C42CE410', X'7001AAFACEAB7B4CACE1170C2B791394', X'7001AAFACEAB7B4CACE1170C2B791394', 3, NULL, NULL, 2, NULL, 0, 'Freeze for at least 6 hours until very solid. Heat enough oil (medium high) to cover the frozen ice cream ball in a wok or saucepan (if you are a confident fryer you can use less oil and move the ball around while frying). Remove the ball from the freezer, unwrap from the foil, check there is no exposed ice cream (if there is cover with another tear of bread) and fry for a minute or two until crispy and golden. <img width=`500` height=`333` src=`https://www.messyvegancook.com/wp-content/uploads/2020/06/IMG_1806-500x333.jpg` class=`attachment-medium size-medium` alt=`Frozen breaded ice cream` srcset=`https://www.messyvegancook.com/wp-content/uploads/2020/06/IMG_1806-500x333.jpg 500w, https://www.messyvegancook.com/wp-content/uploads/2020/06/IMG_1806-1024x683.jpg 1024w, https://www.messyvegancook.com/wp-content/uploads/2020/06/IMG_1806-768x512.jpg 768w, https://www.messyvegancook.com/wp-content/uploads/2020/06/IMG_1806-1536x1024.jpg 1536w, https://www.messyvegancook.com/wp-content/uploads/2020/06/IMG_1806-600x400.jpg 600w, https://www.messyvegancook.com/wp-content/uploads/2020/06/IMG_1806-720x480.jpg 720w, https://www.messyvegancook.com/wp-content/uploads/2020/06/IMG_1806-360x240.jpg 360w, https://www.messyvegancook.com/wp-content/uploads/2020/06/IMG_1806-180x120.jpg 180w, https://www.messyvegancook.com/wp-content/uploads/2020/06/IMG_1806.jpg 1800w` sizes=`(max-width: 500px) 100vw, 500px` />');
INSERT INTO `VWR INTERNAL Messyvegancook ingredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngedient) VALUES (X'74B12F621D20C84AB4DB4B37F6AC6D32', X'7001AAFACEAB7B4CACE1170C2B791394', X'7001AAFACEAB7B4CACE1170C2B791394', 4, NULL, NULL, 2, NULL, 0, 'Shake some icing sugar over the fried ice cream and then drizzle with chocolate sauce and golden syrup. Eat immediately. <img width=`500` height=`333` src=`https://www.messyvegancook.com/wp-content/uploads/2020/06/IMG_1818-500x333.jpg` class=`attachment-medium size-medium` alt=`Vegan Thai Fried Ice Cream ??????????` srcset=`https://www.messyvegancook.com/wp-content/uploads/2020/06/IMG_1818-500x333.jpg 500w, https://www.messyvegancook.com/wp-content/uploads/2020/06/IMG_1818-1024x683.jpg 1024w, https://www.messyvegancook.com/wp-content/uploads/2020/06/IMG_1818-768x512.jpg 768w, https://www.messyvegancook.com/wp-content/uploads/2020/06/IMG_1818-1536x1024.jpg 1536w, https://www.messyvegancook.com/wp-content/uploads/2020/06/IMG_1818-600x400.jpg 600w, https://www.messyvegancook.com/wp-content/uploads/2020/06/IMG_1818-720x480.jpg 720w, https://www.messyvegancook.com/wp-content/uploads/2020/06/IMG_1818-360x240.jpg 360w, https://www.messyvegancook.com/wp-content/uploads/2020/06/IMG_1818-180x120.jpg 180w, https://www.messyvegancook.com/wp-content/uploads/2020/06/IMG_1818.jpg 1800w` sizes=`(max-width: 500px) 100vw, 500px` />');
INSERT INTO `VWR INTERNAL Messyvegancook ingredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngedient) VALUES (X'AEB8F8959697D2438CA4E4B4C652016C', X'943771DC12EDBF4F9A333C52ED79BB88', X'943771DC12EDBF4F9A333C52ED79BB88', 0, NULL, NULL, 2, NULL, 0, 'Simmer the tofu for 15 minutes in salted water. Turn off heat an allow to cool. Drain and leave to dry on kitchen roll.');
INSERT INTO `VWR INTERNAL Messyvegancook ingredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngedient) VALUES (X'7186DE935348D044B43766F69D98E4E1', X'943771DC12EDBF4F9A333C52ED79BB88', X'943771DC12EDBF4F9A333C52ED79BB88', 1, NULL, NULL, 2, NULL, 0, 'Heat oil in a wok. Fry tofu until golden. Drain on kitchen roll.');
INSERT INTO `VWR INTERNAL Messyvegancook ingredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngedient) VALUES (X'7EA701C6E18CB240AA30830CB306FAE5', X'943771DC12EDBF4F9A333C52ED79BB88', X'943771DC12EDBF4F9A333C52ED79BB88', 2, NULL, NULL, 2, NULL, 0, 'Whisk cream and nam prik pao together in a bowl.');
INSERT INTO `VWR INTERNAL Messyvegancook ingredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngedient) VALUES (X'7D0E66ED48EAD84FB04FD00DF5A1120C', X'943771DC12EDBF4F9A333C52ED79BB88', X'943771DC12EDBF4F9A333C52ED79BB88', 3, NULL, NULL, 2, NULL, 0, 'Combine the sugar, soy sauce, seasoning sauce, and water in a another small bowl.');
INSERT INTO `VWR INTERNAL Messyvegancook ingredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngedient) VALUES (X'6E93D28F3B20AA4C916EC27592D8FE49', X'943771DC12EDBF4F9A333C52ED79BB88', X'943771DC12EDBF4F9A333C52ED79BB88', 4, NULL, NULL, 2, NULL, 0, 'Heat oil in wok or pan. Add onion and saute until soft. Then add garlic, ginger, and curry powder. Fry for a minute. Add tofu and fry for another 30 seconds. Add spring onion, chinese celery, and soy sauce mixture. Cook for another 30 seconds.');
INSERT INTO `VWR INTERNAL Messyvegancook ingredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngedient) VALUES (X'888CD21EBF976E429CD99E46EC911370', X'943771DC12EDBF4F9A333C52ED79BB88', X'943771DC12EDBF4F9A333C52ED79BB88', 5, NULL, NULL, 2, NULL, 0, 'Pour milk mixture over and simmer for a a minute or two. Taste and season.');
INSERT INTO `VWR INTERNAL Messyvegancook ingredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngedient) VALUES (X'4DBC45FB0E20FE43B56E5EDAC9339276', X'943771DC12EDBF4F9A333C52ED79BB88', X'943771DC12EDBF4F9A333C52ED79BB88', 6, NULL, NULL, 2, NULL, 0, 'Garnish with sliced chilies, fresh coriander, and sesame seeds (all optional) and serve with steamed jasmine rice.');
INSERT INTO `VWR INTERNAL Messyvegancook ingredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngedient) VALUES (X'78756D8314213741B1BCDE9F0506FA6E', X'9F2181A6984BCA48AF033AD3D8D385BE', X'9F2181A6984BCA48AF033AD3D8D385BE', 0, NULL, NULL, 2, NULL, 0, 'Add broken rice, stock or water, and salt to your instant pot and set the manual option to high for 30 minutes. Cook in sealed position and allow pressure to release naturally when finished. Do not release the pressure manually unless you want rice goop splattered about your kitchen. Check the consistency and add more water and/or stock if desired. You can also cook it on the hob. To do this, add ingredients to a saucepan and bring to the boil. Knock back to a gentle simmer and cook for around an hour, stirring occasionally (be sure to scrape the bottom). For the stovetop method you will need more liquid, which you can top up as you go.');
INSERT INTO `VWR INTERNAL Messyvegancook ingredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngedient) VALUES (X'732D654A855C6E48B80C682DF0EB1FB8', X'90C895F552F9F24384D9214A312A2210', X'90C895F552F9F24384D9214A312A2210', 0, NULL, NULL, 2, NULL, 0, 'Add ingredients to a pot and bring to the boil. Turn down to a low simmer and cook for 45-60 minutes, covered. To make in instant pot: Add all ingredients and cook sealed for 20 minutes on low pressure. Natural release.');
INSERT INTO `VWR INTERNAL Messyvegancook ingredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngedient) VALUES (X'DF7AF101517F9745BF08B139719A19A9', X'90C895F552F9F24384D9214A312A2210', X'90C895F552F9F24384D9214A312A2210', 1, NULL, NULL, 2, NULL, 0, 'Allow the stock to cool a little and strain solids, reserving the stock. Save the mushrooms, which you can use in the next recipe.');
INSERT INTO `VWR INTERNAL Messyvegancook ingredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngedient) VALUES (X'0442D2FDB7BDA347A8F56ADE147E2065', X'BAA94A9391E25444AE80122A7B35540E', X'BAA94A9391E25444AE80122A7B35540E', 0, NULL, NULL, 2, NULL, 0, 'Make sure the mushrooms have been squeezed of as much liquid as possible. Heat at least 2 centimetres worth of vegetable oil for frying to a medium heat in a wok. Fry the mushrooms for 3-5 minutes, until golden. Drain on kitchen towel. When cool, slice into ½ centimetre thick pieces.');
INSERT INTO `VWR INTERNAL Messyvegancook ingredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngedient) VALUES (X'965B5C9D51ECA44D8906CBA1B51F1C1F', X'BAA94A9391E25444AE80122A7B35540E', X'BAA94A9391E25444AE80122A7B35540E', 1, NULL, NULL, 2, NULL, 0, 'Allow the oil to cool and remove all but 2-3 teaspoons worth. Add remaining ingredients and bring to the boil before adding the sliced fried mushrooms. Knock down heat to a medium simmer for 5 or so minutes, until there is very little liquid remaining. The remaining sauce should be syrupy.');
INSERT INTO `VWR INTERNAL Messyvegancook ingredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngedient) VALUES (X'204913A1CB493746A280D4DF955C4BD3', X'7095831067A9D2429D39D2C452106495', X'7095831067A9D2429D39D2C452106495', 0, NULL, NULL, 2, NULL, 0, 'Set up a steamer and bring to the boil. Pop a plate inside if you have one that fill fit (otherwise some foil will do).');
INSERT INTO `VWR INTERNAL Messyvegancook ingredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngedient) VALUES (X'16458C90C3022742911BCFF0450F14B1', X'7095831067A9D2429D39D2C452106495', X'7095831067A9D2429D39D2C452106495', 1, NULL, NULL, 2, NULL, 0, 'Pound coriander stems, garlic, and peppercorns into a paste using a pestle and mortar. Add water, soy sauce, seasoning sauce and oil. Give it a quick mix with the pestle.');
INSERT INTO `VWR INTERNAL Messyvegancook ingredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngedient) VALUES (X'E01E391CF0857D4E8DA25D257E3990C8', X'7095831067A9D2429D39D2C452106495', X'7095831067A9D2429D39D2C452106495', 2, NULL, NULL, 2, NULL, 0, 'Combine remaining (dry) ingredients in a medium bowl. Mix to combine. Add wet mix from mortar and mash together with your hands. It might be a shaggy dough but that’s okay.');
INSERT INTO `VWR INTERNAL Messyvegancook ingredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngedient) VALUES (X'8CF9B0EEB1989946AB1E55F4CC381C36', X'7095831067A9D2429D39D2C452106495', X'7095831067A9D2429D39D2C452106495', 3, NULL, NULL, 2, NULL, 0, 'Pinch bits off into teaspoon (ish) lumps and place in the steamer. Steam for 20-30 minutes.');
INSERT INTO `VWR INTERNAL Messyvegancook ingredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngedient) VALUES (X'FD2B1AB92E0D454F8B23C3BC990C677B', X'FB88706079CD9F488CE9E3A90F2D0A3D', X'FB88706079CD9F488CE9E3A90F2D0A3D', 0, NULL, NULL, 2, NULL, 0, 'Divide the jok between 3-4 bowls (or 2, if you''re very hungry). Dash a few shakes of soy sauce over the porridge. Divide the mushrooms and faux pork between the bowls. Add a handful of chopped spring onion and/or coriander plus a generous tablespoon of finely julienned fresh ginger. Shake some white pepper on the lot and serve. Bring the soy sauce to the table so you can season it further, if need be. You can add any of the additional toppings too. My photo includes fermented bean curd and fried garlic and its oil (but I forgot the mushrooms).');
INSERT INTO `VWR INTERNAL Messyvegancook ingredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngedient) VALUES (X'06918FD66CB14B4184A3B94010BBA3E1', X'6ACFB5625EF0E0439EE7DC6E65C42870', X'6ACFB5625EF0E0439EE7DC6E65C42870', 0, NULL, NULL, 2, NULL, 0, 'Slice the long dried chilies lengthways and remove the seeds. Cut into chunks and soak in warm water until soft.');
INSERT INTO `VWR INTERNAL Messyvegancook ingredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngedient) VALUES (X'84EA107B4FC3BB47B25D5BE5CF83B78C', X'6ACFB5625EF0E0439EE7DC6E65C42870', X'6ACFB5625EF0E0439EE7DC6E65C42870', 1, NULL, NULL, 2, NULL, 0, 'Pound the soaked chilies, dried bird''s eye chilies, and salt using a pestle and mortar until smooth. Add lemongrass and pound into a uniform smooth paste. Continue pounding each ingredient, one at a time, until smooth.');
INSERT INTO `VWR INTERNAL Messyvegancook ingredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngedient) VALUES (X'1178FB5306CB09488B941E1A731DA281', X'6ACFB5625EF0E0439EE7DC6E65C42870', X'6ACFB5625EF0E0439EE7DC6E65C42870', 2, NULL, NULL, 2, NULL, 0, 'Use immediately or store for up to a few weeks in the fridge (or you can freeze it for longer).');
INSERT INTO `VWR INTERNAL Messyvegancook ingredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngedient) VALUES (X'E8BAE66DF9048549BA12DEC5DE22CF87', X'0FD8EB382D91BB49A7622732B934B54D', X'0FD8EB382D91BB49A7622732B934B54D', 0, NULL, NULL, 2, NULL, 0, 'Thin soy sauce refers to basic Thai soy sauce.');
INSERT INTO `VWR INTERNAL Messyvegancook ingredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngedient) VALUES (X'8EBCABAF6BAD8447A685492668BF5B29', X'0FD8EB382D91BB49A7622732B934B54D', X'0FD8EB382D91BB49A7622732B934B54D', 1, NULL, NULL, 2, NULL, 0, 'You do not want to use fresh yuba (soy milk skin) for this recipe. Instead, look for packaged bean curd sheets with Chinese text on the packaging. The sheets must be flexible and not the dried kind that are brittle. This is not the same as tofu skin.');
INSERT INTO `VWR INTERNAL Messyvegancook ingredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngedient) VALUES (X'140A93332E53234B90C275DC3F1018D8', X'0FD8EB382D91BB49A7622732B934B54D', X'0FD8EB382D91BB49A7622732B934B54D', 2, NULL, NULL, 2, NULL, 0, 'Yuba can dry out fairly quickly so keep any pieces you aren’t using in a sealed container.');
INSERT INTO `VWR INTERNAL Messyvegancook ingredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngedient) VALUES (X'AD289D628354A843AAB959B00CBCC6A4', X'0FD8EB382D91BB49A7622732B934B54D', X'0FD8EB382D91BB49A7622732B934B54D', 3, NULL, NULL, 2, NULL, 0, 'You can dip the ½ tablespoon measure in a little bit of oil before scooping the yolk that it releases easily in a uniform shape when you drop it onto the white.');
INSERT INTO `VWR INTERNAL Messyvegancook ingredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngedient) VALUES (X'F5E54898A04A554F91E0432CBD0E552F', X'C224A2ECAB5F654E96A0B14E16B48944', X'C224A2ECAB5F654E96A0B14E16B48944', 0, NULL, NULL, 2, NULL, 0, 'Seperate the noodles. If they have been refrigerated, steam them in a bowl for a few minutes until soft enough to separate.');
INSERT INTO `VWR INTERNAL Messyvegancook ingredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngedient) VALUES (X'1C07C547CFEFCE4CA52159CC780CF4A5', X'C224A2ECAB5F654E96A0B14E16B48944', X'C224A2ECAB5F654E96A0B14E16B48944', 1, NULL, NULL, 2, NULL, 0, 'In a small bowl, combine the soy sauce, seasoning sauce, sweet soy sauce, and sugar.');
INSERT INTO `VWR INTERNAL Messyvegancook ingredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngedient) VALUES (X'2757573A499B614CA42CE8E12E213850', X'C224A2ECAB5F654E96A0B14E16B48944', X'C224A2ECAB5F654E96A0B14E16B48944', 2, NULL, NULL, 2, NULL, 0, 'Heat a wok to high heat. Add the oil. As soon as it smokes, add the garlic and chilli. Stir fry about 15-20 seconds, until garlic begins to brown. Add the onions and stir fry for thirty seconds before adding the protein and stir frying for another minute.');
INSERT INTO `VWR INTERNAL Messyvegancook ingredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngedient) VALUES (X'C00EF7C503D0964BAEF642E21EC57ACB', X'C224A2ECAB5F654E96A0B14E16B48944', X'C224A2ECAB5F654E96A0B14E16B48944', 3, NULL, NULL, 2, NULL, 0, 'Add the noodles, sauce, tomatoes, and green peppercorns, stir frying to coat every strand of noodle. Leave to cook for a minute or two, allowing the noodles to absorb the sauce. Flip and cook for another minute or two.');
INSERT INTO `VWR INTERNAL Messyvegancook ingredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngedient) VALUES (X'EF791A1106B53242843FFCD9B285A423', X'C224A2ECAB5F654E96A0B14E16B48944', X'C224A2ECAB5F654E96A0B14E16B48944', 4, NULL, NULL, 2, NULL, 0, 'Turn off the heat and add the holy basil, quickly stirring to wilt. Serve immediately with chilli vinegar.');
INSERT INTO `VWR INTERNAL Messyvegancook ingredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngedient) VALUES (X'D6AF8F21F44EB84EAD195C26E6C89CA4', X'244812562A8A1B44AAC93421A8A867D1', X'244812562A8A1B44AAC93421A8A867D1', 0, NULL, NULL, 2, NULL, 0, 'Before starting, ensure all utensils and bowls you use are scrupulously clean. Hands too!');
INSERT INTO `VWR INTERNAL Messyvegancook ingredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngedient) VALUES (X'643D959F867C81439A4C74E058F774F0', X'244812562A8A1B44AAC93421A8A867D1', X'244812562A8A1B44AAC93421A8A867D1', 1, NULL, NULL, 2, NULL, 0, 'Cut or tear the oyster mushrooms lengthwise (down the lines of the gills) into thin slivers. Pile them into a steamer and steam for 20-30 minutes on a fairly high heat. They will be fine and stringy, but tough, when finished. Allow to cool enough to handle.');
INSERT INTO `VWR INTERNAL Messyvegancook ingredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngedient) VALUES (X'9CFDB95BDA373A4E9B0AAB393D16590B', X'244812562A8A1B44AAC93421A8A867D1', X'244812562A8A1B44AAC93421A8A867D1', 2, NULL, NULL, 2, NULL, 0, 'While the mushrooms are cooling pound the salt and garlic to a fine paste with a pestle and mortar.');
INSERT INTO `VWR INTERNAL Messyvegancook ingredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngedient) VALUES (X'28154BB75171F84BB29183010F06CA27', X'244812562A8A1B44AAC93421A8A867D1', X'244812562A8A1B44AAC93421A8A867D1', 3, NULL, NULL, 2, NULL, 0, 'Rinse the cooked rice to break up any clumps and squeeze or press it to remove excess water. Pop the rice into the mortar and pound lightly with the garlic paste. The goal isn''t to make it into a paste, but to break the grains down only slightly.');
INSERT INTO `VWR INTERNAL Messyvegancook ingredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngedient) VALUES (X'035FABB6F680ED4EAFC6B8484F02CDF4', X'244812562A8A1B44AAC93421A8A867D1', X'244812562A8A1B44AAC93421A8A867D1', 4, NULL, NULL, 2, NULL, 0, 'Once the steamed mushroom shreds have cooled you must rid them of excess moisture by wringing them out in muslin or another thin cloth. Pressing in small portions will make this easier. Wring as much liquid out as possible (tip: save the liquid and use as a replacement for vegetable stock in recipes).');
INSERT INTO `VWR INTERNAL Messyvegancook ingredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngedient) VALUES (X'5BC1D5FD2CB0184C8DF30F3145C3CF5F', X'244812562A8A1B44AAC93421A8A867D1', X'244812562A8A1B44AAC93421A8A867D1', 5, NULL, NULL, 2, NULL, 0, 'Combine the rice and garlic paste with the mushrooms in a bowl and knead with your hands to combine. Divide them into four portions and wrap them in squares of cling film to form small sausage shapes.');
INSERT INTO `VWR INTERNAL Messyvegancook ingredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngedient) VALUES (X'2520582E71A83041B5651ADAD45997FE', X'244812562A8A1B44AAC93421A8A867D1', X'244812562A8A1B44AAC93421A8A867D1', 6, NULL, NULL, 2, NULL, 0, 'Remember, these would be fermented at temperatures around or above 30 degrees Celsius in Thailand, so find a warm spot to leave them to ferment. In the winter I often wrap them in a tea towel and leave them under a radiator. Another option is an airing cupboard. In ideal conditions these could be done in 24 hours, but I find anywhere from 3-7 days is right in the English climate. They should taste salty, slightly sour, and pungent from garlic when done.');
INSERT INTO `VWR INTERNAL Messyvegancook ingredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngedient) VALUES (X'F9A4F1ECA4765F48860D709329DE5BAB', X'244812562A8A1B44AAC93421A8A867D1', X'244812562A8A1B44AAC93421A8A867D1', 7, NULL, NULL, 2, NULL, 0, 'You can eat them raw or fry them briefly, which is my preference because it imparts some oiliness that pairs well with the sides you should consume the sausages with: deep fried cashews and/or peanuts, raw cabbage, thinly sliced ginger, bird''s eye chillies, chopped shallots, fresh lime, and sprigs of coriander.');
INSERT INTO `VWR INTERNAL Messyvegancook ingredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngedient) VALUES (X'CC80995894C0F540A1878881D6771C11', X'9071EF1FC9D3AB4EBC2222800CDADC8E', X'9071EF1FC9D3AB4EBC2222800CDADC8E', 0, NULL, NULL, 2, NULL, 0, 'Combine the fermented tofu, soy sauces, seasoning sauce, and sugar in a small cup or bowl, using a fox to mash the tofu.');
INSERT INTO `VWR INTERNAL Messyvegancook ingredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngedient) VALUES (X'CAE265A94916DC4C95805E85DE9EF7F7', X'9071EF1FC9D3AB4EBC2222800CDADC8E', X'9071EF1FC9D3AB4EBC2222800CDADC8E', 1, NULL, NULL, 2, NULL, 0, 'Heat a wok to high and add the oil. When it becomes to smoke, add the garlic and sliced chilies, stir frying until fragrant and the garlic begins to lightly brown (15-30 seconds). Add the seitan or crumbled tofu and stir fry rapidly for a minute or two until hot and starting to brown.');
INSERT INTO `VWR INTERNAL Messyvegancook ingredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngedient) VALUES (X'B3675B36A3D03E49B7E252221ABBF338', X'9071EF1FC9D3AB4EBC2222800CDADC8E', X'9071EF1FC9D3AB4EBC2222800CDADC8E', 2, NULL, NULL, 2, NULL, 0, 'Tip the sauce in, and quickly stir fry to combine. After a minute, turn the heat off and tip the holy basil leaves into your wok. Continue to toss the contents until the leaves wilt slightly. Serve immediately with 17 tonnes of white rice and chili vinegar, and ideally with a vegan fried egg (see notes3). Season with white pepper.');
