INSERT INTO `VWR INTERNAL Messyvegancook ingredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngedient) VALUES (X'31D5120A2C760B43B603C83F630BAD7A', X'D9D5BE57BE9178459CE7329D6AC0D221', X'D9D5BE57BE9178459CE7329D6AC0D221', 1, NULL, NULL, 2, NULL, 0, 'Toast the cumin and coriander seeds over medium heat by shaking them around in a pan for a minute or two until lightly toasted and fragrant. Grind to a powder with a pestle and mortar, along with the white peppercorns, then remove to a smaller bowl.');
INSERT INTO `VWR INTERNAL Messyvegancook ingredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngedient) VALUES (X'3F07653C1B6BC1499675DC4D99E4054C', X'D9D5BE57BE9178459CE7329D6AC0D221', X'D9D5BE57BE9178459CE7329D6AC0D221', 2, NULL, NULL, 2, NULL, 0, 'Pound the salt and soaked chilies to a paste in the mortar, followed by the lemongrass. Continue in the order of ingredients listed, ensuring each is pounded into a smooth paste before adding the next. Once the shallot has broken down into the paste, add the previously ground spices and nutmeg, if using.');
